# Know when this box was created
date > /etc/vagrant_box_build_time

# Clean out old partial lists
rm -rf /var/lib/apt/lists/*
# Get a clean list of packages
apt-get update -qq
# Upgrade to the latest packages
apt-get -y upgrade
# Install packages we need
apt-get -y install build-essential zlib1g-dev libssl-dev libreadline-gplv2-dev git-core moreutils dkms nfs-common ruby1.9.1 ruby1.9.1-dev libruby1.9.1 python-software-properties virt-what
# Install kernel headers for all the kernels we have
apt-get -y install $(ls /lib/modules | awk '{print "linux-headers-" $0}')
gem install -v "= 1.6.5" bundler --no-ri --no-rdoc

PLATFORM=$(/usr/sbin/virt-what)
echo "Detected platform: ${PLATFORM}"
# Installing the virtualbox guest additions
if [ "$PLATFORM" = "virtualbox" ]; then
  VBOX_VERSION=$(cat /home/vagrant/.vbox_version)
  mkdir -p /mnt/cdrom
  mount -o loop /home/vagrant/VBoxGuestAdditions_$VBOX_VERSION.iso /mnt/cdrom
  sh /mnt/cdrom/VBoxLinuxAdditions.run
  umount /mnt/cdrom
  rm /home/vagrant/VBoxGuestAdditions_$VBOX_VERSION.iso
fi
if [ "$PLATFORM" = "vmware" ]; then
  mkdir -p /mnt/cdrom
  mount -o loop /home/vagrant/linux.iso /mnt/cdrom
  tar zxf /mnt/cdrom/VMwareTools-*.tar.gz -C /tmp/
  /tmp/vmware-tools-distrib/vmware-install.pl -d
  sed -i 's/answer AUTO_KMODS_ENABLED no/answer AUTO_KMODS_ENABLED yes/g' /etc/vmware-tools/locations
  sed -i 's/answer AUTO_KMODS_ENABLED_ANSWER no/answer AUTO_KMODS_ENABLED_ANSWER yes/g' /etc/vmware-tools/locations
  /usr/bin/vmware-config-tools.pl -d
  rm -rf /tmp/vmware-tools-distrib
  umount /mnt/cdrom
  rm /home/vagrant/linux.iso
fi

# Add GOV.UK PPA for Hiera eYAML GPG package
cat <<EOF >/etc/apt/sources.list.d/govuk-ppa.list
deb [arch=amd64] http://apt.production.alphagov.co.uk/govuk/ppa/production precise main
EOF
sudo apt-key adv --recv-keys --keyserver keys.gnupg.net 37E3ACBB

# Install Puppet
. /etc/lsb-release
adduser --system --group --home /var/lib/puppet puppet
TMPFILE=$(mktemp)
wget -qO ${TMPFILE} http://apt.puppetlabs.com/puppetlabs-release-${DISTRIB_CODENAME}.deb
dpkg -i ${TMPFILE}
rm ${TMPFILE}
apt-get update -qq
apt-get install -y puppet='3.4.*' puppet-common='3.4.*' facter='1.7.5*' ruby-hiera-eyaml-gpg

# Make sure our ruby is 1.9.3p0 again after Puppet has required ruby1.8
update-alternatives --set ruby /usr/bin/ruby1.9.1

# Setup sudo to allow no-password sudo for "admin"
groupadd -r admin
usermod -a -G admin vagrant
cp /etc/sudoers /etc/sudoers.orig
sed -i -e '/Defaults\s\+env_reset/a Defaults\texempt_group=admin' /etc/sudoers
sed -i -e 's/%admin ALL=(ALL) ALL/%admin ALL=NOPASSWD:ALL/g' /etc/sudoers

# Installing vagrant keys
mkdir /home/vagrant/.ssh
chmod 700 /home/vagrant/.ssh
cd /home/vagrant/.ssh
wget --no-check-certificate 'https://raw.github.com/mitchellh/vagrant/master/keys/vagrant.pub' -O authorized_keys
chmod 600 /home/vagrant/.ssh/authorized_keys
chown -R vagrant /home/vagrant/.ssh

# Finally, preinstall GitHub keys
cat >>/etc/ssh/ssh_known_hosts <<EOM
# github.com SSH-2.0-OpenSSH_5.5p1 Debian-6+squeeze1+github8
github.com ssh-dss 
# github.com SSH-2.0-OpenSSH_5.5p1 Debian-6+squeeze1+github8
github.com ssh-rsa 
# github.gds SSH-2.0-OpenSSH_5.8p1 Debian-1ubuntu3+github1
github.gds ssh-dss 
# github.gds SSH-2.0-OpenSSH_5.8p1 Debian-1ubuntu3+github1
github.gds ssh-rsa 
EOM

# Remove items used for building, since they aren't needed anymore
apt-get -y autoremove
apt-get clean

# Zero out the free space to save space in the final image:
dd if=/dev/zero of=/EMPTY bs=1M
rm -f /EMPTY

# Removing leftover leases and persistent rules
echo "cleaning up dhcp leases"
rm /var/lib/dhcp3/*

# Make sure Udev doesn't block our network
# http://6.ptmc.org/?p=164
echo "cleaning up udev rules"
rm /etc/udev/rules.d/70-persistent-net.rules
mkdir /etc/udev/rules.d/70-persistent-net.rules
rm -rf /dev/.udev/
rm /lib/udev/rules.d/75-persistent-net-generator.rules

echo "Adding a 2 sec delay to the interface up, to make the dhclient happy"
echo "pre-up sleep 2" >> /etc/network/interfaces

# Remove myself
rm -f /home/vagrant/postinstall.sh

exit
